mapper.select:
select id, sname, phone, email, dept_id, post_id, status, level
from staff
where id = #{id,jdbcType=INTEGER}

select id, sname, phone, email, dept_id, post_id, status, level
from staff
where dept_id = #{id,jdbcType=INTEGER}

select id, sname, phone, email, dept_id, post_id, status, level
from staff
where post_id = #{id,jdbcType=INTEGER}

select id, sname, phone, email, dept_id, post_id, status, level
    from staff
where status = #{status,jdbcType=VARCHAR}

mapper.update:
update staff
set status = #{param,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, sname, phone, email, dept_id deptId, post_id postId, status, level
    from staff
where sname = #{sname,jdbcType=VARCHAR}
